/**
 * @file Holding model
 * @author Sam DeCoursey <samdecoursey@ksu.edu>
 * @exports HoldingSchema the schema for the Holding model
 */

// Import libraries
import Sequelize from "sequelize";

/**
 * @swagger
 * components:
 *   schemas:
 *     Holding:
 *       type: object
 *       required:
 *         - UserID
 *         - AssetID
 *         - Amount
 *         - AveragePrice
 *       properties:
 *         UserID:
 *           type: integer
 *           description: autogenerated id
 *         AssetID:
 *           type: integer
 *           description: autogenerated id
 *         Amount:
 *           type: decimal
 *           description: amount user is holding
 *         AveragePrice:
 *           type: decimal
 *           description: average cost basis
 *       example:
 *           UserID: 1
 *           AssetID: 1
 *           Amount: 120.27
 *           AveragePrice: 14.2
 */
const HoldingSchema = {
  UserID: {
    type: Sequelize.INTEGER,
    foreignKey: true,
  },
  AssetID: {
    type: Sequelize.INTEGER,
    foreignKey: true,
  },
  Amount: {
    type: Sequelize.DECIMAL,
    allowNull: false,
  },
  AveragePrice: {
    type: Sequelize.DECIMAL,
    allowNull: false,
  },
};

export default HoldingSchema;
