/**
 * @file Transaction model
 * @author Sam DeCoursey <samdecoursey@ksu.edu>
 * @exports TransactionSchema the schema for the Transaction model
 */

// Import libraries
import Sequelize from "sequelize";

/**
 * @swagger
 * components:
 *   schemas:
 *     Transaction:
 *       type: object
 *       required:
 *         - UserID
 *         - AssetID
 *         - Amount
 *         - Price
 *         - Type
 *         - Date
 *       properties:
 *         UserID:
 *           type: integer
 *           description: autogenerated id
 *         AssetID:
 *           type: integer
 *           description: autogenerated id
 *         Amount:
 *           type: decimal
 *           description: amount bought/sold
 *         Price:
 *           type: decimal
 *           description: asset price for transaction
 *         Type:
 *           type: boolean
 *           description: true = buy, false = sell
 *         Date:
 *           type: date
 *           description: timestamp of transaction
 *       example:
 *           UserID: 1
 *           AssetID: 1
 *           Amount: 120.27
 *           Price: 14.2
 *           Type: true
 *           Date: "timestamp"
 */
const TransactionSchema = {
  UserID: {
    type: Sequelize.INTEGER,
    foreignKey: true,
  },
  AssetID: {
    type: Sequelize.INTEGER,
    foreignKey: true,
  },
  Amount: {
    type: Sequelize.DECIMAL,
    allowNull: false,
  },
  Price: {
    type: Sequelize.DECIMAL,
    allowNull: false,
  },
  Type: {
    type: Sequelize.BOOLEAN,
    allowNull: false,
  },
  Date: {
    type: Sequelize.DATE,
    allowNull: false,
  },
};

export default TransactionSchema;
